#[derive(Debug)] pub struct Recipe { pub name: String, pub ingredients: Vec<String>, pub instructions: Vec<String>, pub prep_time: Option<u32>, pub cook_time: Option<u32> } impl Recipe { pub fn find(_name: &str) -> Result<Recipe, String> { Ok(Recipe { name: "Simple Pasta".to_string(), ingredients: vec!["pasta".to_string()], instructions: vec!["cook".to_string()], prep_time: Some(5), cook_time: Some(15) }) } }
